import React, { Component, useState } from 'react';
import axios from  'axios';
import logo from './LogoCompasso-positivo.png';
import './App.css';

import Profile from './Profile.js'
import Repo from './Repo.js'


function App() {
  const config = {
    github: {
      url: "https://api.github.com/users",
      cliente_id: "a78fc78be480f3b6c2d6",
      cliente_secret: "0a8d95cb7226c95fbc4acacf992a612e1bc1b983",
      count: 7, 
      sort: "created: asc",
    }
  };

  const [ fieldValue, setFieldValue ] = useState("");
  const [ user, setUser ] = useState(null);
  const [ repos, setRepos ] = useState([]);

  const getUser = () => {
    const { url, cliente_id, cliente_secret, count, sort} = this.state.github;
      try{
      axios.get(
        `${url}/${fieldValue}?client_id=${cliente_id}&cliente_secret=${cliente_secret}`
      ).then(({ data }) => setUser(data));

      axios.get(
        `${url}/${fieldValue}/repos?per_page=${count}&sort=${sort}&client_id=${cliente_id}&client_secret=${cliente_secret}`
      ).then(({ data }) => setRepos(data));
    } catch(error){
      console.log(error)
  }

  const handleChangeField = (e) => {
    setFieldValue (e.target.value) 
  }

  const renderProfile = () => {
    // const { user, repos } = this.state;

    return (
      <div className="row">
        <div className="col-md-4">
          <Profile user={user} />
        </div>
        <div className="col-md-8">
          {repos.map(repo => <Repo key={repo.name} repo={repo}/>)}
        </div>
      </div>
    )
  }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>

        <div className="container">
          <div className="card card-body">
            <p className="lead"> Type a name to find users and repositories </p>
            <input onChange={ handleChangeField } id="search" type="text" className="form-control" required />
          </div>
          <div className="card-body">
            <button onClick={ getUser } className="btn btn-warning btn-block">Ver Perfil</button>
          </div>
        </div>     

        {this.state.user.length !== 0 ? renderProfile() : null}  
      </div>
    );
  }

}

export default App;
