import React, { Component } from 'react';
import axios from  'axios';
import logo from './LogoCompasso-positivo.png';
import './App.css';

// import Profile from './Profile.js'
// import Repo from './Repo.js'

class App extends Component {
  constructor () {
    super();
    this.state = {
      github: {
        url: "https://api.github.com/users",
        cliente_id: "ec6748e0bcd334962de4",
        cliente_secret: "717a85dbf26772f6b4e352217018e7a12d105424",
        count: 7, 
        sort: "created: asc",
      },
      user: [],
      repos: [],
    };
  }

  getUser = (e) => {
    const user = e.target.value;
    const { url, cliente_id, cliente_secret, } = this.state.github;
      axios.get(
        `${url}/${user}?client_id=${cliente_id}&cliente_secret=${cliente_secret}`
      ).then(({ data }) => this.setState({ user: data }));
  }

  render() {
    const { user } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>

        <div className="container">
          <div className="card card-body">
            <p className="lead"> Type a name to find users and repositories </p>
            <input  id="search" type="text" className="form-control" required />
          </div>
        </div>     


      </div>
    );
  }

}

export default App;
